package com.ejercicios.tema2;

public class Ejercicio_00 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * Ejercicio: Comparar i++ y ++i
         *Declara una variable i con valor 10.
         *Usa i++ y ++i en dos operaciones diferentes y muestra los resultados
         * 
         * */
		
		int i = 10;
		System.out.println("valor de i es de "+i);
		System.out.println("valor de i++ mientras se hace la operacion "+ i++);
		System.out.println("valor de i tras haber hecho lo de la linea anterior es de "+i); /*pedir un repaso de este ejercicio*/
		System.out.println();
		 i = 10; //no puedo poner lo del int
		System.out.println("valor de i es de "+i);
		System.out.println("valor de ++i mientras se hace la operacion "+ ++i);
		System.out.println("valor de i tras haber hecho lo de la linea anterior es de "+i);
		
		 i = 20;
		 System.out.println("valor de i es de "+i);
		
	}

}
