package ejerciciosParaPracticar1Tema;

public class feliz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int año = 2025; 
		if (año == 2024 + 1){
		 System.out.println("Feliz 2025"); 
		} else if (año == 2025 - 1){
		 System.out.println("Feliz año nuevo");
		} else { 
		System.out.println("Próspero año"); 
		} 

	}

}
